#This shell produces overview stats in a single line format, suitable for logscape ingestion$wait = 30 Add-PSSnapIn Microsoft.HPC;while(1) {	$OUT = Get-HpcClusterOverview     foreach ($RECORD in $OUT) {     $timestamp = Get-Date -Format "yyyy/MM/dd HH:mm:ss zzz"                    $LOG =  $timestamp + "," + $RECORD.ClusterName + "," + $RECORD.Version + "," + $RECORD.TotalJobCount + "," + $RECORD.SubmittedJobCount + "," + $RECORD.QueuedJobCount + "," + $RECORD.RunningJobCount + "," + $RECORD.FinishingJobCount + "," +  $RECORD.FinishedJobCount + "," + $RECORD.CancelingJobCount + "," + $RECORD.CanceledJobCount + "," +  $RECORD.ValidatingJobCount + "," + $RECORD.TotalTaskCount + "," + $RECORD.SubmittedTaskCount + "," + $RECORD.QueuedTaskCount + "," + $RECORD.RunningTaskCount + "," + $RECORD.FinishedTaskCount + "," + $RECORD.CancelingTaskCount + "," + $RECORD.CanceledTaskCount + "," + $RECORD.TotalCoreCount + "," + $RECORD.BusyCoreCount + "," + $RECORD.IdleCoreCount + "," + $RECORD.OfflineCoreCount + "," + $RECORD.TotalNodeCount + "," + $RECORD.ReadyNodeCount + "," + $RECORD.DrainingNodeCount + "," + $RECORD.OfflineNodeCount + "," + $RECORD.UnreachableNodeCount            Write-Host $LOG         }	Sleep $wait}