$duration = 60$wait = $durationAdd-PSSnapIn Microsoft.HPC;while(1) {        $SINCE = (get-date).addseconds(-1 * $duration)        #Write-Host ">>> getting:"	$OUT = get-winevent -logname  "Microsoft-HPC-Scheduler/Operational" | where {$_.timecreated -ge $SINCE}    #$OUT = get-winevent -logname "Windows HPC Server" | where {$_.timecreated -ge $THEN}        foreach ($RECORD in $OUT)     {        if (!$RECORD.Message.Contains("was modified by")) {             $timestamp = $RECORD.TimeCreated.ToString("yyyy/MM/dd HH:mm:ss zzz")            $LOG = $timestamp  + "," + $RECORD.Id  + "," + $RECORD.LevelDisplayName  + "," + $RECORD.Message             Write-Host $LOG        }     }     #Write-Host ">>> sleep:"		Sleep $wait}